import React, { useState } from 'react';
import './App.css';
import PetGallery from './components/PetGallery';
import { petsData } from './data/petsData';

function App() {
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  const filteredPets = petsData.filter(pet => {
    const matchesFilter = filter === 'all' || pet.type.toLowerCase() === filter.toLowerCase();
    const matchesSearch = pet.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         pet.breed.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesFilter && matchesSearch;
  });

  return (
    <div className="App">
      <header className="app-header">
        <h1>üêæ Pet Adoption Gallery</h1>
        <p>Find your perfect furry companion!</p>
      </header>

      <div className="filters-container">
        <div className="search-container">
          <input
            type="text"
            placeholder="Search by name or breed..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>

        <div className="filter-buttons">
          <button 
            className={filter === 'all' ? 'filter-btn active' : 'filter-btn'}
            onClick={() => setFilter('all')}
          >
            All Pets
          </button>
          <button 
            className={filter === 'dog' ? 'filter-btn active' : 'filter-btn'}
            onClick={() => setFilter('dog')}
          >
            üêï Dogs
          </button>
          <button 
            className={filter === 'cat' ? 'filter-btn active' : 'filter-btn'}
            onClick={() => setFilter('cat')}
          >
            üê± Cats
          </button>
          <button 
            className={filter === 'rabbit' ? 'filter-btn active' : 'filter-btn'}
            onClick={() => setFilter('rabbit')}
          >
            üê∞ Rabbits
          </button>
        </div>
      </div>

      <PetGallery pets={filteredPets} />

      <footer className="app-footer">
        <p>üíù Every pet deserves a loving home</p>
      </footer>
    </div>
  );
}

export default App;
