import React from 'react';
import './PetCard.css';

const PetCard = ({ pet, onAdopt, isAdopted }) => {
  const handleAdopt = () => {
    if (isAdopted) {
      alert(`${pet.name} has already been adopted! Thank you for your interest.`);
      return;
    }
    onAdopt(pet.id);
    alert(`Congratulations! You've successfully adopted ${pet.name}! üéâ Please contact the adoption center to complete the process.`);
  };

  const getTypeIcon = (type) => {
    switch(type.toLowerCase()) {
      case 'dog': return 'üêï';
      case 'cat': return 'üê±';
      case 'rabbit': return 'üê∞';
      case 'fish': return 'üê†';
      case 'rat': return 'üêÄ';
      case 'guinea pig': return 'üêπ';
      case 'hamster': return 'üêπ';
      case 'ferret': return 'ü¶´';
      case 'bird': return 'üê¶';
      case 'bearded dragon': return 'ü¶é';
      case 'chinchilla': return 'üê≠';
      case 'turtle': return 'üê¢';
      case 'snake': return 'üêç';
      case 'horse': return 'üêé';
      case 'leopard gecko': return 'ü¶é';
      case 'tarantula': return 'üï∑Ô∏è';
      default: return 'üêæ';
    }
  };

  const getAgeColor = (age) => {
    if (age <= 1) return 'young';
    if (age <= 5) return 'adult';
    return 'senior';
  };

  return (
    <div className="pet-card">
      <div className="pet-image-container">
        <img src={pet.image} alt={pet.name} className="pet-image" />
        <div className="pet-type-badge">
          {getTypeIcon(pet.type)}
        </div>
      </div>
      
      <div className="pet-info">
        <h3 className="pet-name">{pet.name}</h3>
        <p className="pet-breed">{pet.breed}</p>
        
        <div className="pet-details">
          <span className={`pet-age ${getAgeColor(pet.age)}`}>
            {pet.age} year{pet.age !== 1 ? 's' : ''} old
          </span>
          <span className="pet-gender">{pet.gender}</span>
        </div>

        <div className="pet-traits">
          {pet.traits && pet.traits.map((trait, index) => (
            <span key={index} className="trait-tag">{trait}</span>
          ))}
        </div>

        <p className="pet-description">{pet.description}</p>

        <div className="pet-location">
          üìç {pet.location}
        </div>
      </div>

      <div className="pet-actions">
        <button className="adopt-btn" onClick={handleAdopt}>
          üíï Adopt Me
        </button>
        <button className="learn-more-btn">
          ‚ÑπÔ∏è Learn More
        </button>
      </div>
    </div>
  );
};

export default PetCard;
